import React, {useState} from 'react';


const todo = props => {

    const [todoName, setTodoName] = useState({
        name : 'Ahsan Farooq',
        class : 'BESE-7B'
    });

    const inputChangeHandler = (event) => {

        let newTodo = {
            ...todoName,
            name : 'Muhammad Ahsan Farooq',
            class : 'Graduated'
        }
        setTodoName(newTodo)
    }

    console.log(todoName)

    return (
        <React.Fragment>
            <input 
                type = 'text' 
                placeholder = 'Todo'
                value = {todoName.name}
                onChange = {inputChangeHandler} />
            <button type = "button">Add</button>
            <ul>
                <li>
                    {todoName.name}
                </li>
                <li>
                    {todoName.class}
                </li>
            </ul>
        </React.Fragment>
    )

}

export default todo;
